import UsersList from '../components/UsersList';
import Layout from '../components/Layout';
import fetch from 'isomorphic-fetch';


class users extends Comment.React{
    render(){
        return(
            <Layout>
                <div>
                    <h1>Users page</h1> <br/>
                    <UsersList users={props.users}></UsersList>
                </div>
            </Layout>
        )
    }
}
